DROP TABLE IF EXISTS absentee;
CREATE TABLE absentee (county_desc char(20) COMMENT "County description",voter_reg_num char(12) COMMENT "Voter registration number",ncid char(12) COMMENT "NC identifier  (added: 01/23/2015)",voter_last_name char(30) COMMENT "Last name of registered voter",voter_first_name char(60) COMMENT "First name ",voter_middle_name char(20) COMMENT "Middle name",race char(60) COMMENT "Race",gender char(60) COMMENT "Gender/sex",age char(3) COMMENT "Age",voter_street_address char(60) COMMENT "Street address",voter_city char(60) COMMENT "City		",voter_state char(2) COMMENT "State",voter_zip char(9) COMMENT "Zip code",ballot_mail_street_address char(60) COMMENT "Ballot mailed street address",ballot_mail_city char(60) COMMENT "Ballot mailed city",ballot_mail_state char(2) COMMENT "Ballot mailed state",ballot_mail_zip char(9) COMMENT "Ballot mailed zip code",other_mail_addr1 char(60) COMMENT "Other street address of registered voter",other_mail_addr2 char(60) COMMENT "Other street address - second line",other_city_state_zip char(60) COMMENT "Other address city, state, and zip code",election_dt char(10) COMMENT "Election date",voter_party_code char(3) COMMENT "Party of registered voter",precinct_desc char(30) COMMENT "Precinct name",cong_dist_desc char(30) COMMENT "Congressional district name",nc_house_desc char(30) COMMENT "NC House district name",nc_senate_desc char(30) COMMENT "NC Senate district name",ballot_req_delivery_type char(30) COMMENT "Ballot delivery method type",ballot_req_type char(30) COMMENT "Ballot request type",ballot_request_party char(3) COMMENT "Ballot request party",ballot_req_dt char(10) COMMENT "Ballot request date",ballot_send_dt char(10) COMMENT "Ballot send date",ballot_rtn_dt char(10) COMMENT "Ballot return date",ballot_rtn_status char(30) COMMENT "Ballot return status",site_name char(50) COMMENT "Site name  (added: 05/06/2014)",SDR char(3) COMMENT "Same Day Registrant (added: 11/13/2014)",mail_verification_status char(20) COMMENT "Mail Verification Status of New Registrants (150 days prior to election date) (added: 11/14/2016)");
